using System.Collections;
using UnityEngine;

public class Hero : MonoBehaviour
{
    float hp = 100;
    private bool isBossSpawner = false;

    private float attackSpeed = 1f;
    private bool isDead = false;
    [SerializeField]private float attackCooldown = 1f;
    [SerializeField]private float attackRange = 1f;
    [SerializeField]private LayerMask enemyLayer; 
    private float attackTimer = 0f;

    private void Start()
    {
        attackTimer = 0f;
        StartCoroutine(Attack());
    }

    IEnumerator Attack()
    {
        while (!isDead)
        {
            Collider2D[] colliders = Physics2D.OverlapCircleAll(transform.position, attackRange,enemyLayer);
            bool enemyInRange = colliders.Length > 0;
            if (!enemyInRange)
            {
                // Previously used `continue` here which caused a tight loop without yielding
                // and froze the editor when no enemies were present. Yield each frame
                // to allow Unity to update and avoid locking the main thread.
                yield return null;
                continue;
            }
            Debug.Log("Attack");
            yield return new WaitForSeconds(attackCooldown);
        }
    }


    private void OnCollisionEnter2D(Collision2D collision)
    {
        if (collision.collider.CompareTag("Enemy1"))
        {
            hp = hp - 5;
        }
        if (collision.collider.CompareTag("Enemy2"))
        {
            hp = hp - 10;
        }
        if (collision.collider.CompareTag("Enemy3"))
        {
            hp = hp - 15;
        }
    }
}

